SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hbb_obter_qtde_notas_ipe (nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

 
 
ie_tipo_protocolo_w		smallint;
qt_notas_w			bigint:=	0;

/*	1-	Quantidade de Notas 	*/
 
 

BEGIN 
 
select 	max(ie_tipo_protocolo) 
into STRICT	ie_tipo_protocolo_w 
from 	protocolo_convenio 
where 	nr_seq_protocolo = nr_seq_protocolo_p;
 
if (ie_tipo_protocolo_w = 2) then	 
	select 	max(nr_folha) 
	into STRICT	qt_notas_w 
	from	w_interf_conta_item_ipe 
	where 	nr_seq_protocolo = nr_seq_protocolo_p;
else 
	select 	count(*) 
	into STRICT	qt_notas_w 
	from 	conta_paciente 
	where 	nr_seq_protocolo = nr_seq_protocolo_p;
end if;
 
RETURN qt_notas_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
