CREATE OR REPLACE FUNCTION hbo_obter_guia_proc_mat (nr_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE




cd_autorizacao_w	varchar(20);
ds_retorno_w		varchar(255);



BEGIN

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') then

	select	max(a.cd_autorizacao)
	into STRICT	cd_autorizacao_w
	from	autorizacao_convenio a,
		procedimento_autorizado b
	where	a.nr_seq_autorizacao	= b.nr_seq_autorizacao
	and	a.nr_atendimento	= b.nr_atendimento
	and	a.nr_atendimento	= nr_atendimento_p
	and	b.cd_procedimento	= cd_procedimento_p
	and	b.ie_origem_proced	= ie_origem_proced_p;

elsif (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	max(a.cd_autorizacao)
	into STRICT	cd_autorizacao_w
	from	autorizacao_convenio a,
		material_autorizado b
	where	a.nr_seq_autorizacao	= b.nr_seq_autorizacao
	and	a.nr_atendimento	= b.nr_atendimento
	and	a.nr_atendimento	= nr_atendimento_p
	and	b.cd_material		= cd_material_p;

end if;

ds_retorno_w	:= cd_autorizacao_w;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
