SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hbo_obter_total_protocolo ( nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_total_protocolo_w	double precision := 0;


BEGIN 
 
/*Este function foi criada específica para um cliente. A mesma retorna a soma das contas do protocolo, somando apenas os insumos, ou seja, os itens que não estão no pacote*/
 
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then 
	begin 
	select	coalesce(sum(vl_item), 0) 
	into STRICT	vl_total_protocolo_w 
	from ( 
		SELECT	sum(b.vl_material) vl_item 
		from	material_atend_paciente b, 
			conta_paciente a 
		where	a.nr_interno_conta = b.nr_interno_conta 
		and	a.nr_seq_protocolo = nr_seq_protocolo_p 
		and	coalesce(b.nr_seq_proc_pacote::text, '') = '' 
		
union all
 
		SELECT	sum(b.vl_procedimento) vl_item 
		from	procedimento_paciente b, 
			conta_paciente a 
		where	a.nr_interno_conta = b.nr_interno_conta 
		and	a.nr_seq_protocolo = nr_seq_protocolo_p 
		and	coalesce(b.nr_seq_proc_pacote::text, '') = '' 
		) alias7;
	end;
end if;
 
return vl_total_protocolo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION hbo_obter_total_protocolo ( nr_seq_protocolo_p bigint) FROM PUBLIC;

