CREATE OR REPLACE FUNCTION hcc_obter_valor_exame ( cd_tipo_procedimento_p bigint, cd_convenio_p bigint, vl_exame_p bigint) RETURNS bigint AS $body$
DECLARE


vl_exame_w	double precision;


BEGIN

if (cd_convenio_p not in (1,28,37)) then
	if (cd_tipo_procedimento_p in (1,3,4,5,97)) then
