	elsif (cd_tipo_procedimento_p = 15) then
		vl_exame_w	:= (vl_exame_p * 0.12);
	end if;
elsif (cd_convenio_p in (1,28,37)) then
	if (cd_tipo_procedimento_p in (1,2,3,4,5,15,97)) then
		vl_exame_w	:= (vl_exame_p * 0.10);
	end if;
end if;

Return coalesce(vl_exame_w,vl_exame_p);

END;
