CREATE OR REPLACE FUNCTION hcl_obter_maior_consumo_dia ( cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_material_p bigint, dt_mesano_referencia_p timestamp, dt_inicial_p timestamp, qt_dias_passados_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			double precision;


BEGIN

select max(qt_movimento)
	into STRICT	ds_retorno_w
from (	SELECT	sum(a.qt_consumo) qt_movimento
	from  	movimento_estoque_v a,
		material m
	where 		a.cd_material_estoque  	= m.cd_material_estoque
	and 		m.cd_material          	= cd_material_p
	and 		dt_mesano_referencia   	= dt_mesano_referencia_p
	and 		a.cd_estabelecimento   	= cd_estabelecimento_p
	and		a.cd_local_estoque	= cd_local_estoque_p
	and 		(cd_centro_custo IS NOT NULL AND cd_centro_custo::text <> '')
	and 		dt_movimento_estoque   	between trunc(dt_inicial_p,'dd') - qt_dias_passados_p and dt_inicial_p
	group by 		trunc(dt_movimento_estoque, 'dd')) alias5;

return  ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
