SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hc_colocar_dr_dra_nome_medic ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255);
nm_pessoa_w	varchar(255);
ie_medico_w	varchar(20);
ie_sexo_w	varchar(1);

BEGIN
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	select	max(SUBSTR(OBTER_NOME_PF(CD_PESSOA_FISICA),0,255)), 
		max(substr(obter_se_medico(cd_pessoa_fisica, 'M'), 1, 10)), 
		max(ie_sexo) 
	into STRICT	nm_pessoa_w, 
		ie_medico_w, 
		ie_sexo_w 
	from 	pessoa_fisica 
	where	cd_pessoa_fisica = cd_pessoa_fisica_p;
 
	if (ie_medico_w = 'S' AND ie_sexo_w = 'M') then 
		ds_retorno_w := 'Dr. '||nm_pessoa_w;
	elsif (ie_medico_w = 'S' AND ie_sexo_w = 'F') then 
		ds_retorno_w := 'Dra. '||nm_pessoa_w;
	else 
		ds_retorno_w := nm_pessoa_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
