SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hc_consistir_dispon_func (cd_profissional_p bigint, dt_agendamento_p timestamp, nr_minuto_duracao_p bigint) RETURNS varchar AS $body$
DECLARE


/*	Valores do DS_RETORNO_W
	N=Não possui inconsistência
	E=Inconsistência na Escala de Plantões e Sobreavisos.
	A=Possuir registro de ausencia do profissional.
	H=Já possui agendamento para a data e hora informado.*/
ds_retorno_w 		varchar(1)  	:= 'N';
ie_dispon_func_w	varchar(1) 	:= 'N';
ie_possui_escala_w	varchar(1)	:= 'N';
nr_seq_escala_w		bigint;
nr_dia_semana_w		smallint;
ie_final_semana_w	varchar(1)	:= 'N';
cd_estabelecimento_w	estabelecimento.cd_estabelecimento%type := wheb_usuario_pck.get_cd_estabelecimento;

c01 CURSOR FOR
	SELECT	coalesce(d.nr_seq_escala,0)
	from	escala_horario_medico e,
		escala_horario_medico_lib d,
		escala_classif c,
		escala_grupo b,
		escala a
	where	c.nr_sequencia = b.nr_seq_classif
	and 	e.nr_sequencia = d.nr_seq_escala_horario
	and	b.nr_sequencia = a.nr_seq_grupo
	and	a.nr_sequencia = d.nr_seq_escala
	and	c.ie_tipo_escala = 'H'
	and	e.cd_pessoa_fisica = cd_profissional_p
	group by nr_seq_escala;


BEGIN

if (cd_profissional_p IS NOT NULL AND cd_profissional_p::text <> '') and (dt_agendamento_p IS NOT NULL AND dt_agendamento_p::text <> '') then
	--Escalas de Plantões e Sobreavisos.
	open c01;
	loop
	fetch c01 into
		nr_seq_escala_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (nr_seq_escala_w > 0) and (ie_possui_escala_w = 'N') then

			--busca dia da semana;
			select 	PKG_DATE_UTILS.get_WeekDay(dt_agendamento_p)
			into STRICT	nr_dia_semana_w
			;

			--consiste escala de trabalho do profissional.
			select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
			into STRICT	ie_dispon_func_w
			from	escala_horario_medico_lib
			where	nr_seq_escala = nr_seq_escala_w
			and	((ie_dia_semana = nr_dia_semana_w)
				or (ie_dia_semana = 0)--todos os dias
				or ((ie_dia_semana = 10) and (pkg_date_utils.IS_BUSINESS_DAY(dt_agendamento_p)  = 0))--final de semana.
				or ((ie_dia_semana = 9) and (pkg_date_utils.IS_BUSINESS_DAY(dt_agendamento_p)  = 1)))--dias de trabalho.
			and (coalesce(ie_feriado,'N') = 'N'
				or (ie_feriado = 'S' and obter_se_feriado(cd_estabelecimento_w,dt_agendamento_p) > 0)
				or (ie_feriado = 'F' and obter_se_feriado(cd_estabelecimento_w,dt_agendamento_p) = 0))
			and	dt_agendamento_p between
				PKG_DATE_UTILS.get_Time(dt_agendamento_p, hr_inicial_vinc, 0) and
				PKG_DATE_UTILS.get_Time(dt_agendamento_p, hr_final_vinc, 0);

			if (ie_dispon_func_w = 'S') then
				ie_possui_escala_w := 'S';
			end if;
		end if;
		end;

	end loop;
	close c01;
	if (ie_possui_escala_w = 'N') then
		ds_retorno_w := 'E';
	end if;
	--Registro de ausência dos profissionais  (AUSENCIA_TASY) pasta Ausência da função Escalas de Plantões e Sobreavisos
	if (ie_dispon_func_w = 'S') then
		select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_dispon_func_w
		from   	ausencia_tasy
		where  	nm_usuario_ausente = Obter_Nome_Usuario_cod(cd_profissional_p)
		and    	dt_agendamento_p between dt_inicio and dt_fim;

		if (ie_dispon_func_w = 'N') then
			ds_retorno_w := 'A';
		end if;
	end if;
	--Agendamentos do Home Care (AGENDA_HC_PACIENTE)
	if (ie_dispon_func_w = 'S') then
		select 	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ie_dispon_func_w
		from   	hc_profissional a,
			hc_agenda_prof b,
			agenda_hc_paciente c
		where  	a.cd_pessoa_fisica = cd_profissional_p
		and	a.nr_sequencia = b.nr_seq_prof_hc
		and	b.nr_seq_agenda = c.nr_sequencia
		and 	(dt_agendamento_p between c.dt_agenda  and (c.dt_agenda + (coalesce(c.NR_MINUTO_DURACAO,0)/1440))
			or   ((dt_agendamento_p + (coalesce(nr_minuto_duracao_p,0)/1440))   between c.dt_agenda  and (c.dt_agenda + (coalesce(c.NR_MINUTO_DURACAO,0)/1440))));
		--and	dt_agendamento_p  between c.dt_agenda and (c.dt_agenda + (nvl(c.NR_MINUTO_DURACAO,0)/1440));
		if (ie_dispon_func_w = 'N') then
			ds_retorno_w := 'H';
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION hc_consistir_dispon_func (cd_profissional_p bigint, dt_agendamento_p timestamp, nr_minuto_duracao_p bigint) FROM PUBLIC;

