CREATE OR REPLACE FUNCTION hc_obter_atendimento_agenda ( nr_seq_agenda_p bigint) RETURNS bigint AS $body$
DECLARE


nr_atendimento_w	bigint;


BEGIN
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then

	select	max(b.nr_atendimento_origem)
	into STRICT	nr_atendimento_w
	from	agenda_hc_paciente a,
		paciente_home_care b
	where	a.nr_seq_paciente_hc = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_agenda_p
