CREATE OR REPLACE FUNCTION hc_obter_atend_home_care_exter (cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

 
nr_atendimento_w	bigint;


BEGIN 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	 
	select	max(x.nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	atend_categoria_convenio a, 
		atendimento_paciente x 
	where	1 = 1 
	and	a.nr_atendimento	= x.nr_atendimento 
	and	a.ie_tipo_guia		= 'H' 
	and	coalesce(x.dt_alta::text, '') = '' 
	and	x.ie_tipo_atendimento	= 7 
	and	x.cd_pessoa_fisica	= cd_pessoa_fisica_p;
 
end if;
 
return	nr_atendimento_w;
 
end;
