SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hc_obter_dados_guia (nr_seq_guia_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(255);
ds_carater_w		varchar(255);
cd_guia_w		varchar(20);
ie_consulta_urg_w		varchar(1);
ds_tipo_guia_w		varchar(255);
nm_prestador_w		varchar(255);
ds_estagio_guia_w		varchar(255);


BEGIN 
 
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then 
 
	select	max(substr(obter_valor_dominio(1016,IE_CARATER_INTERNACAO),1,255)), 
		max(cd_guia), 
		MAX(coalesce(ie_consulta_urgencia,'N')), 
		max(substr(obter_valor_dominio(1746,ie_tipo_guia),1,255)), 
		max(substr(pls_obter_dados_prestador(nr_seq_prestador,'N'),1,255)), 
		max(obter_valor_dominio(2055,ie_estagio)) 
	into STRICT	ds_carater_w, 
		cd_guia_w, 
		ie_consulta_urg_w, 
		ds_tipo_guia_w, 
		nm_prestador_w, 
		ds_estagio_guia_w 
	from	pls_guia_plano b, 
		pls_segurado a 
	where	a.nr_sequencia = b.nr_seq_segurado 
	and	b.nr_sequencia = nr_seq_guia_p;
 
end if;
 
if (ie_retorno_p = 'CI') then 
	ds_retorno_w := ds_carater_w;
elsif (ie_retorno_p = 'CG') then 
	ds_retorno_w := cd_guia_w;
elsif (ie_retorno_p = 'UR') then 
	ds_retorno_w := coalesce(ie_consulta_urg_w,'N');
elsif (ie_retorno_p = 'TG') then 
	ds_retorno_w := ds_tipo_guia_w;
elsif (ie_retorno_p = 'NP') then 
	ds_retorno_w := nm_prestador_w;
elsif (ie_retorno_p = 'EG') then 
	ds_retorno_w := ds_estagio_guia_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
