CREATE OR REPLACE FUNCTION hc_obter_dados_paciente (nr_seq_paciente_hc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ie_classificacao_w	paciente_home_care.ie_classificacao%type;
				

BEGIN

if (nr_seq_paciente_hc_p IS NOT NULL AND nr_seq_paciente_hc_p::text <> '') then

	select	ie_classificacao
	into STRICT	ie_classificacao_w
	from	paciente_home_care
	where	nr_sequencia = nr_seq_paciente_hc_p;

	if (ie_opcao_p = 'CS') then
		ds_retorno_w := ie_classificacao_w;
	end if;
	
end if;

return	ds_retorno_w;

end;
$body$
