SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hc_obter_dados_regiao_pac ( nr_seq_paciente_hc_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

--ie_opcao_p
--	'R' = Descrição da região
--	'S' = Descrição da sub-região
ds_retorno_w		varchar(90);
ds_regiao_w		varchar(90);
ds_sub_regiao_w		varchar(90);


BEGIN

if (nr_seq_paciente_hc_p IS NOT NULL AND nr_seq_paciente_hc_p::text <> '') then
	begin

	select	substr(obter_desc_regiao_home_care(a.nr_seq_regiao),1,90),
		substr(obter_desc_sub_regiao_hc(a.nr_seq_sub_regiao),1,90)
	into STRICT	ds_regiao_w,
		ds_sub_regiao_w
	from	hc_como_chegar_paciente a
	where	nr_seq_paciente	= nr_seq_paciente_hc_p;

	if (ie_opcao_p = 'R') then
		begin
		ds_retorno_w := ds_regiao_w;
		end;
	end if;
	if (ie_opcao_p = 'S') then
		begin
		ds_retorno_w := ds_sub_regiao_w;
		end;
	end if;
	end;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
