SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hc_obter_procedimento ( nr_seq_paciente_hc_p bigint, nr_seq_servico_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_procedimento_w	varchar(255);
ie_exibir_procedimentos_w		varchar(1);

C01 CURSOR FOR
	SELECT	distinct substr(obter_desc_procedimento(b.cd_procedimento,b.ie_origem_proced),1,250)
	from	hc_paciente_servico a,
		hc_paciente_servico_proc b
	where	a.nr_sequencia	  = b.nr_seq_servico
	and	((ie_exibir_procedimentos_w = 'N')
		or (a.nr_seq_pac_home_care = nr_seq_paciente_hc_p))
	and	a.nr_seq_servico = nr_seq_servico_p;


BEGIN
ie_exibir_procedimentos_w := obter_param_usuario(867, 91, obter_perfil_ativo, Wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_exibir_procedimentos_w);

if (coalesce(nr_seq_paciente_hc_p,0) > 0) then
	begin

	open C01;
	loop
	fetch C01 into
		ds_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := ds_procedimento_w;
		else
			ds_retorno_w := substr(ds_retorno_w || ', ' || ds_procedimento_w,1,255);
		end if;

		end;
	end loop;
	close C01;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
