SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hc_obter_proc_prof ( nr_seq_prof_hc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_procedimento_w	varchar(255);

C01 CURSOR FOR
	SELECT	substr(obter_desc_procedimento(a.cd_procedimento,a.ie_origem_proced),1,250)
	from	hc_paciente_servico_proc a,
		hc_profissional b
	where	b.cd_procedimento = a.cd_procedimento
	and	b.nr_sequencia	  = nr_seq_prof_hc_p;


BEGIN
if (nr_seq_prof_hc_p IS NOT NULL AND nr_seq_prof_hc_p::text <> '') then
	begin

	open C01;
	loop
	fetch C01 into
		ds_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w := ds_procedimento_w;
		else
			ds_retorno_w := substr(ds_retorno_w || ', ' || ds_procedimento_w,1,255);
		end if;

		end;
	end loop;
	close C01;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
