CREATE OR REPLACE FUNCTION hc_obter_regiao_paciente (NR_SEQ_PAC_HC_P bigint) RETURNS varchar AS $body$
DECLARE


DS_RETORNO_W	varchar(90);


BEGIN

SELECT 	SUBSTR(OBTER_DESC_REGIAO_HOME_CARE(NR_SEQ_REGIAO),1,90)
INTO STRICT	DS_RETORNO_W
FROM	PACIENTE_HOME_CARE A,
	HC_COMO_CHEGAR_PACIENTE B
WHERE	A.NR_SEQUENCIA = B.NR_SEQ_PACIENTE
AND	A.NR_SEQUENCIA = NR_SEQ_PAC_HC_P;

RETURN	DS_RETORNO_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
