CREATE OR REPLACE FUNCTION hc_obter_se_atraso_agenda (qt_min_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1) := 'N';
dt_agenda_w	timestamp;
qt_min_real_w	bigint;

BEGIN
select	dt_agenda
into STRICT	dt_agenda_w
from	agenda_hc_paciente
where	nr_sequencia = nr_seq_agenda_p;

if (dt_agenda_w = clock_timestamp()) then
	ds_retorno_w := 'N';
elsif (dt_agenda_w > clock_timestamp()) then
	select	round(((dt_agenda) -(clock_timestamp()))*24*60)
	into STRICT	qt_min_real_w
	from	agenda_hc_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (qt_min_real_w > qt_min_p) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
elsif (dt_agenda_w < clock_timestamp()) then
	select	round(((clock_timestamp())-(dt_agenda))*24*60)
	into STRICT	qt_min_real_w
	from	agenda_hc_paciente
	where	nr_sequencia = nr_seq_agenda_p;

	if (qt_min_real_w > qt_min_p) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
