CREATE OR REPLACE FUNCTION hc_obter_se_paciente_obito (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
	begin

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	paciente_home_care	a,
		pessoa_fisica		b
	where	a.cd_pessoa_fisica 	= b.cd_pessoa_fisica
	and	a.nr_sequencia 		= nr_seq_paciente_p
	and	(b.dt_obito IS NOT NULL AND b.dt_obito::text <> '');
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
