CREATE OR REPLACE FUNCTION hc_obter_se_paciente_regiao ( CD_PESSOA_FISICA_p text, nr_seq_regiao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN

ds_retorno_w	:= 'S';

if (CD_PESSOA_FISICA_p IS NOT NULL AND CD_PESSOA_FISICA_p::text <> '') and (coalesce(nr_seq_regiao_p,0) > 0) then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	PACIENTE_HOME_CARE a,
		HC_COMO_CHEGAR_PACIENTE b
	where	a.nr_sequencia = b.nr_seq_paciente
	and	CD_PESSOA_FISICA = CD_PESSOA_FISICA_p
	and	nr_seq_regiao = nr_seq_regiao_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
