SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hc_obter_status_paciente ( nr_seq_paciente_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

				 
ie_retorno_w				varchar(1);				
ie_parecer_w	   		varchar(1);
nr_atendimento_origem_w		bigint;
ie_prim_avaliacao_lib_w		varchar(1);

/* 
E - Em atendimento 
*/
 
 

BEGIN 
ie_retorno_w:= 'N';
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then 
 
	select 	max(nr_atendimento_origem), 
			max(ie_parecer) 
	into STRICT	nr_atendimento_origem_w, 
			ie_parecer_w 
	from  	paciente_home_care 
	where 	nr_sequencia = nr_seq_paciente_p;
 
	if (nr_atendimento_origem_w IS NOT NULL AND nr_atendimento_origem_w::text <> '' AND ie_parecer_w = 'A') then 
	 
		SELECT 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_prim_avaliacao_lib_w 
		FROM  	paciente_home_care a, 
				atendimento_checklist b, 
				med_avaliacao_paciente c, 
				checklist_processo d, 
				checklist_processo_item e, 
				checklist_processo_regra f 
		where 	a.nr_sequencia		 = b.nr_seq_paciente_hc  
		and	  	b.nr_sequencia		 = c.nr_seq_atend_checklist 
		and	  	b.nr_seq_checklist	 = d.nr_sequencia 
		and	  	d.nr_sequencia		 = e.nr_seq_checklist 
		and	  	d.nr_sequencia		 = f.nr_seq_checklist 
		AND	  	f.ie_momento 		 = 'P' 
		AND	  	a.nr_sequencia		 = nr_seq_paciente_p 
		AND	  	(c.dt_liberacao IS NOT NULL AND c.dt_liberacao::text <> '');
	 
		if (ie_prim_avaliacao_lib_w = 'S') then 
			ie_retorno_w:= 'S';
		end if;
	end if;
 
	if (ie_retorno_w = 'N') then 
 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_retorno_w 
		from	paciente_home_care	a, 
				paciente_hc_atend	b 
		where	a.nr_sequencia = b.nr_seq_paciente 
		and		a.nr_sequencia = nr_seq_paciente_p;
 
	end if;	
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION hc_obter_status_paciente ( nr_seq_paciente_p bigint, ie_opcao_p text) FROM PUBLIC;

