CREATE OR REPLACE FUNCTION hc_pad_obter_interv_dias_prof (nr_seq_controle_p bigint) RETURNS varchar AS $body$
DECLARE

dt_inicial_w	timestamp;
dt_final_w	timestamp;
qt_retorno_w	bigint;

BEGIN
if (nr_seq_controle_p IS NOT NULL AND nr_seq_controle_p::text <> '') then
	select	max(dt_inicial),
		max(dt_final)
	into STRICT	dt_inicial_w,
		dt_final_w
	from	hc_pad_controle
	where	nr_sequencia = nr_seq_controle_p;

	if (dt_inicial_w IS NOT NULL AND dt_inicial_w::text <> '') and (dt_final_w IS NOT NULL AND dt_final_w::text <> '') then
		qt_retorno_w := (trunc(dt_final_w) - trunc(dt_inicial_w))+1;
	end if;
end if;
return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
