SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hdjb_obter_dados_tiss_diag ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p= '1'  - TISS Tipo Doença
    	     = '2'  - TISS Tempo Doença
	     = '3'  - TISS Unidade Tempo */
ie_tipo_doenca_w	varchar(2);
ie_tipo_tempo_doenca_w	varchar(2);
qt_tempo_doenca_w	bigint;
ds_retorno_w		varchar(255);


BEGIN

begin
select	ie_tipo_doenca,
	ie_unidade_tempo,
	qt_tempo
into STRICT	ie_tipo_doenca_w,
	ie_tipo_tempo_doenca_w,
	qt_tempo_doenca_w
from	diagnostico_medico
where	nr_atendimento = nr_atendimento_p
and 	dt_diagnostico = (SELECT max(dt_diagnostico)
			  from   diagnostico_medico
			  where  nr_atendimento = nr_atendimento_p);
exception
	when others then
	ie_tipo_doenca_w:= ' ';
	ie_tipo_tempo_doenca_w:= ' ';
	qt_tempo_doenca_w:= 0;
end;

if (ie_opcao_p = '1') then
	ds_retorno_w:= ie_tipo_doenca_w;
elsif (ie_opcao_p = '2') then
	ds_retorno_w:= ie_tipo_tempo_doenca_w;
elsif (ie_opcao_p = '3') then
	ds_retorno_w:= to_char(qt_tempo_doenca_w);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
