CREATE OR REPLACE FUNCTION hdjf_obter_se_medico_credenc ( cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, cd_convenio_p bigint, cd_prestador_p text, cd_especialidade_p bigint, cd_categoria_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



ie_medico_credenciado_w		varchar(1);
cd_setor_atendimento_w		integer;

C01 CURSOR FOR
	SELECT	coalesce(ie_conveniado,'N')
	from	medico_convenio
	where	cd_pessoa_fisica		= cd_pessoa_fisica_p
	and	cd_convenio			= cd_convenio_p
	and	((cd_estabelecimento = cd_estabelecimento_p) or (coalesce(cd_estabelecimento::text, '') = ''))
	and 	((coalesce(cd_prestador,'0')	= coalesce(cd_prestador_p, '0')) or (coalesce(cd_prestador::text, '') = ''))
--	and 	((nvl(cd_especialidade,0) = nvl(cd_especialidade_p,0)) or (cd_especialidade is null))
	and 	((coalesce(cd_categoria::text, '') = '') or (coalesce(cd_categoria,'0') 	= coalesce(cd_categoria_p,'0')))
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w
	order by
		coalesce(cd_estabelecimento,0),
		coalesce(cd_especialidade,0),
		somente_numero(coalesce(cd_prestador,'0')),
		somente_numero(coalesce(cd_categoria,'0')),
		coalesce(cd_setor_atendimento,0);


BEGIN

ie_medico_credenciado_w			:= 'N';
cd_setor_atendimento_w			:= coalesce(cd_setor_atendimento_p,0);

OPEN C01;
LOOP
FETCH C01 into
	ie_medico_credenciado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_medico_credenciado_w		:= ie_medico_credenciado_w;
END LOOP;
CLOSE C01;

RETURN ie_medico_credenciado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
