CREATE OR REPLACE FUNCTION hdm_get_qt_vital_sign_var ( nr_dif_person_p text, dt_start_p timestamp, dt_end_p timestamp, si_option_p text) RETURNS bigint AS $body$
DECLARE


qt_variation_w	bigint;


BEGIN

if (si_option_p = 'BMI_RED') then
	select	CASE WHEN sign(SUM(a.qt_bmi_range_variation))=-1 THEN 1  ELSE 0 END  / count(1)
	into STRICT	qt_variation_w
	from	hdm_indic_dm_day b,
		hdm_indic_ft_vital_sign a
	where	a.nr_seq_day = b.nr_sequencia
	and	b.dt_complete_date between dt_start_p and dt_end_p
	and	a.nr_dif_person = nr_dif_person_p;
elsif (si_option_p = 'BMI_RAI') then
	select	CASE WHEN sign(SUM(a.qt_bmi_range_variation))=1 THEN 1  ELSE 0 END  / count(1)
	into STRICT	qt_variation_w
	from	hdm_indic_dm_day b,
		hdm_indic_ft_vital_sign a
	where	a.nr_seq_day = b.nr_sequencia
	and	b.dt_complete_date between dt_start_p and dt_end_p
	and	a.nr_dif_person = nr_dif_person_p;
elsif (si_option_p = 'BP_RED') then
	select	CASE WHEN sign(SUM(a.qt_bp_range_variation))=-1 THEN 1  ELSE 0 END  / count(1)
	into STRICT	qt_variation_w
	from	hdm_indic_dm_day b,
		hdm_indic_ft_vital_sign a
	where	a.nr_seq_day = b.nr_sequencia
	and	b.dt_complete_date between dt_start_p and dt_end_p
	and	a.nr_dif_person = nr_dif_person_p;
elsif (si_option_p = 'BP_RAI') then
	select	CASE WHEN sign(SUM(a.qt_bp_range_variation))=1 THEN 1  ELSE 0 END  / count(1)
	into STRICT	qt_variation_w
	from	hdm_indic_dm_day b,
		hdm_indic_ft_vital_sign a
	where	a.nr_seq_day = b.nr_sequencia
	and	b.dt_complete_date between dt_start_p and dt_end_p
	and	a.nr_dif_person = nr_dif_person_p;
elsif (si_option_p = 'GLYC_RED') then
	select	CASE WHEN sign(SUM(a.qt_glyc_range_variation))=-1 THEN 1  ELSE 0 END  / count(1)
	into STRICT	qt_variation_w
	from	hdm_indic_dm_day b,
		hdm_indic_ft_vital_sign a
	where	a.nr_seq_day = b.nr_sequencia
	and	b.dt_complete_date between dt_start_p and dt_end_p
	and	a.nr_dif_person = nr_dif_person_p;
elsif (si_option_p = 'GLYC_RAI') then
	select	CASE WHEN sign(SUM(a.qt_glyc_range_variation))=1 THEN 1  ELSE 0 END  / count(1)
	into STRICT	qt_variation_w
	from	hdm_indic_dm_day b,
		hdm_indic_ft_vital_sign a
	where	a.nr_seq_day = b.nr_sequencia
	and	b.dt_complete_date between dt_start_p and dt_end_p
	and	a.nr_dif_person = nr_dif_person_p;
end if;

return	coalesce(qt_variation_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
