SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hdp_obter_ano_guia_conv ( nr_atendimento_p bigint, cd_convenio_p bigint) RETURNS timestamp AS $body$
DECLARE

			 
qt_contas_w	smallint;
ds_retorno_w	timestamp;


BEGIN 
 
select	count(*) 
into STRICT	qt_contas_w 
from	atendimento_paciente a, 
	conta_paciente c 
where	a.nr_atendimento 	= c.nr_atendimento 
and	a.nr_atendimento 	= nr_atendimento_p 
and	c.cd_convenio_parametro = cd_convenio_p;
 
if (qt_contas_w = 1) then 
	 
	select	max(dt_inicio_vigencia) 
	into STRICT	ds_retorno_w 
	from	atend_categoria_convenio 
	where	nr_atendimento 	= nr_atendimento_p 
	and	cd_convenio	= cd_convenio_p;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
