CREATE OR REPLACE FUNCTION hdp_obter_data_atend_ipe ( nr_atendimento_p bigint, nr_interno_Conta_p bigint) RETURNS timestamp AS $body$
DECLARE

 
nm_pessoa_fisica_w	varchar(100);
dt_entrada_w		timestamp;
ie_existe_w		varchar(1);

BEGIN
 
nm_pessoa_fisica_w	:= upper(obter_pessoa_atendimento(nr_atendimento_p,'N'));
 
select	max(dt_entrada) 
into STRICT	dt_entrada_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
if (position('RN' in nm_pessoa_fisica_w)	> 0) then 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_existe_w 
	 
	where	exists (	SELECT	1 
				from	atend_paciente_unidade a 
				where	a.nr_atendimento	= nr_atendimento_p 
				and	a.cd_setor_Atendimento	= 14);
	if (ie_existe_w	= 'S') then 
		select	coalesce(dt_periodo_inicial,dt_entrada_w) 
		into STRICT	dt_entrada_w 
		from	conta_paciente 
		where	nr_interno_conta	= nr_interno_Conta_p;
	end if;
end if;
 
return	dt_entrada_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
