SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_consulta_se_qt_medic_disp ( ds_lista_param_p text, qt_mdedicamento_p bigint) RETURNS varchar AS $body$
DECLARE

tam_lista_w		bigint;
ie_pos_virgula_w	smallint;
ie_pos_traco_w		bigint;
lista_inf_w		varchar(100);
nr_seq_medic_w		numeric(20);
nulo_w			numeric(20);
nr_lote_w		numeric(20);
ds_retorno_w		varchar(1) := 'N';
qt_saldo_w		bigint;

BEGIN
if (ds_lista_param_p IS NOT NULL AND ds_lista_param_p::text <> '') then
	tam_lista_w		:= length(ds_lista_param_p);
	ie_pos_virgula_w	:= position(',' in ds_lista_param_p);
	ie_pos_traco_w		:= position('-' in ds_lista_param_p);
	lista_inf_w		:= substr(ds_lista_param_p,1,ie_pos_virgula_w - 1);

	/* obter a sequÃªncia lida */

	if (ie_pos_virgula_w <> 0) then
		begin
		nulo_w			:= substr(lista_inf_w,1,(ie_pos_traco_w - 1));
		lista_inf_w		:= substr(lista_inf_w,ie_pos_traco_w + 1,100);
		nr_lote_w		:= lista_inf_w;
		end;
	end if;

	if (coalesce(nr_lote_w,0) > 0) then

		select  substr(hd_obter_qt_saldo_lote(nr_sequencia),1,255)
		into STRICT	qt_saldo_w
		from	hd_lote_medic_ext
		where	nr_sequencia = nr_lote_w;

		if (qt_mdedicamento_p > qt_saldo_w) then
			ds_retorno_w := 'N';
		else
			ds_retorno_w := 'S';
		end if;
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
