CREATE OR REPLACE FUNCTION hd_desc_marca_osmose (nr_seq_osmose_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_marca_w	varchar(255);



BEGIN

IF (coalesce(nr_seq_osmose_p,0) > 0) THEN

	SELECT 	ds_marca
	INTO STRICT	ds_marca_w
	FROM	hd_marca_osmose
	WHERE	nr_sequencia = nr_seq_osmose_p;

END IF;

IF (ie_opcao_p = 'D') THEN

	ds_retorno_w	:= ds_marca_w;

END IF;

RETURN ds_retorno_w;

END;
