SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_habilita_teste_reuso ( nr_seq_dialisador_p bigint, nr_teste_p bigint) RETURNS varchar AS $body$
DECLARE

qt_reg_w	bigint;
ie_retorno_w	varchar(1) := 'N';

BEGIN
if (nr_teste_p = 1) then
	select	count(*)
	into STRICT	qt_reg_w
	from	hd_teste_reuso
	where	nr_seq_dialisador = nr_seq_dialisador_p
	and		coalesce(nr_seq_dialise::text, '') = '';
	
	if (qt_reg_w = 0) then						
		ie_retorno_w := 'S';
	else
		select	count(*)
		into STRICT	qt_reg_w
		from	hd_teste_reuso
		where	nr_seq_dialisador = nr_seq_dialisador_p
		and		nr_teste = 1
		and		ie_resultado = 'N'
		and		dt_teste = (SELECT	max(dt_teste)
							from	hd_teste_reuso
							where	nr_seq_dialisador = nr_seq_dialisador_p);
		if (qt_reg_w > 0) then
			ie_retorno_w := 'S';
		end if;
	end if;
	
elsif (nr_teste_p = 2) then
	select	count(*)
	into STRICT	qt_reg_w
	from	hd_teste_reuso
	where	nr_seq_dialisador = nr_seq_dialisador_p
	and		nr_teste = 1
	and		coalesce(nr_seq_dialise::text, '') = ''
	and		ie_resultado = 'P'
	and		not exists (SELECT	1
						from	hd_teste_reuso
						where	nr_seq_dialisador = nr_seq_dialisador_p
						and		nr_teste = 2
						and		coalesce(nr_seq_dialise::text, '') = ''
						and		ie_resultado = 'N');
	if (qt_reg_w > 0) then
		ie_retorno_w := 'S';
	end if;
end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
