CREATE OR REPLACE FUNCTION hd_obter_acesso_concat ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE



nr_seq_acesso_w		bigint;
ds_tecnica_acesso_w	varchar(80);
ds_local_acesso_w	varchar(80);

ds_retorno_w		varchar(160);




c01 CURSOR FOR
	SELECT	nr_sequencia
	from	hd_acesso
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	dt_instalacao <= clock_timestamp()
	and	coalesce(dt_perda_retirada::text, '') = '';



BEGIN


open C01;
loop
	fetch C01 into
		nr_seq_acesso_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	substr(c.ds_tecnica_acesso,1,80),
			substr(b.ds_local_acesso,1,80)
		into STRICT	ds_tecnica_acesso_w,
			ds_local_acesso_w
		from	hd_tecnica_acesso c,
			hd_local_acesso b,
			hd_acesso a
		where	c.nr_sequencia	    	= a.nr_seq_tecnica
		and	b.nr_sequencia 	    	= a.nr_seq_local
		and	a.nr_sequencia		= nr_seq_acesso_w;

		ds_retorno_w	:= ds_tecnica_acesso_w || ' ' || ds_local_acesso_w || ', ' || ds_retorno_w;

		end;

end loop;
close C01;

ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w) - 2) || '.';


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
