CREATE OR REPLACE FUNCTION hd_obter_atend_pac_unid ( nr_seq_unid_origem_p bigint, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

 
cd_estabelecimento_w	bigint;
ds_retorno_w		bigint;
			

BEGIN 
 
select	cd_estabelecimento 
into STRICT	cd_estabelecimento_w 
from	hd_unidade_dialise 
where	nr_sequencia		= nr_seq_unid_origem_p;
 
select 	coalesce(max(nr_atendimento),0) 
into STRICT	ds_retorno_w 
from	atendimento_paciente 
where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	cd_estabelecimento	= cd_estabelecimento_w 
and	coalesce(dt_alta::text, '') = '';
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
