CREATE OR REPLACE FUNCTION hd_obter_cancelamento_paciente ( cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE


dt_cancelamento_w		timestamp;


BEGIN

begin
select max(dt_cancelamento)
into STRICT	 dt_cancelamento_w
from	 hd_dialise
where	 cd_pessoa_fisica	= cd_pessoa_fisica_p;
end;

return to_char(dt_cancelamento_w, 'dd/mm/yyyy hh24:mi:ss');

end;
$body$
