CREATE OR REPLACE FUNCTION hd_obter_classif_sor_pac (cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(100);
nr_seq_classificacao_w	varchar(10);
ds_classificacao_w		varchar(100);
ds_classif_prioridade_w	varchar(90);

BEGIN

select	max(b.nr_seq_classificacao)
into STRICT	nr_seq_classificacao_w
from	hd_classificacao_sorologia a,
	hd_paciente_classif_sor b
where	b.nr_seq_classificacao = a.nr_sequencia
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(b.dt_fim::text, '') = ''
and	a.ie_prioridade = (SELECT	min(x.ie_prioridade)
		            from	hd_classificacao_sorologia x,
				hd_paciente_classif_sor y
		            where	y.nr_seq_classificacao = x.nr_sequencia
		            and              y.cd_pessoa_fisica = cd_pessoa_fisica_p
		            and	coalesce(y.dt_fim::text, '') = '');

select	max(ds_classificacao)
into STRICT	ds_classificacao_w
from	hd_classificacao_sorologia
where	nr_sequencia	= nr_seq_classificacao_w;

if (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_classificacao_w;
elsif (ie_opcao_p	= 'C') then
	ds_retorno_w	:= nr_seq_classificacao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
