SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_cor_ageserv ( cd_classificacao_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_fonte_w	varchar(10);
ds_cor_fundo_w	varchar(10);
ds_retorno_w	varchar(10) := '';


BEGIN
if (cd_classificacao_p IS NOT NULL AND cd_classificacao_p::text <> '') then
	select 	max(ds_cor_fonte),
			max(ds_cor_fundo)
	into STRICT	ds_cor_fonte_w,
			ds_cor_fundo_w
	from 	agenda_classif
	where 	cd_classificacao = cd_classificacao_p;

	if (ie_opcao_p = 'FO') then
		ds_retorno_w := ds_cor_fonte_w;
	elsif (ie_opcao_p = 'FU')	then
		ds_retorno_w := ds_cor_fundo_w;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
