CREATE OR REPLACE FUNCTION hd_obter_cor_fora ( nr_sequencia_p bigint, ie_atributo_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_fora_w    varchar(15);
ds_valor_w    varchar(20);
nm_atributo_w    varchar(255);
ds_numero_w        integer;
ds_resultado_w    varchar(255);


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (ie_atributo_p IS NOT NULL AND ie_atributo_p::text <> '') then

  nm_atributo_w := 'DS_RESULTADO'||ie_atributo_p;
  ds_valor_w := obter_valor_dinamico('select ' || nm_atributo_w ||
			' 	from hd_grid_controle_dialise
				where nm_usuario = ' ||chr(39)||nm_usuario_p||chr(39)||
			' 	and nr_sequencia = ' ||chr(39)||nr_sequencia_p||chr(39), ds_valor_w);

  select is_number(ds_valor_w)
  into STRICT ds_numero_w
;

  if  (ds_valor_w IS NOT NULL AND ds_valor_w::text <> '' AND ds_numero_w <> 0) then
  
     EXECUTE 'select ' || nm_atributo_w ||
			' 	from hd_grid_controle_dialise where nm_usuario = ' ||chr(39)||nm_usuario_p||chr(39)||
			' 	and ie_ordem = 1 ' into STRICT ds_resultado_w;
         
      select distinct ds_cor_fora_padrao
      into STRICT  ds_cor_fora_w
      from  HD_ITEM_CONTROLE_EXAME ,
      hd_grid_controle_dialise
      where ds_item = ds_resultado_w
      and (ds_valor_w)::numeric  >= qt_valor_maximo;

  end if;

end if;

return ds_cor_fora_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
