SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_dados_entrega (nr_seq_entrega_p bigint, ie_tipo_dado_p text) RETURNS bigint AS $body$
DECLARE


/*
'S' = Quantidade Saldo
'T' = Quantidade Total
*/
nr_seq_lote_w	bigint;
qt_medic_w	bigint;
qt_utilizado_w	bigint;
qt_utilizado_ww	bigint	:= 0;
qt_retorno_w	bigint;


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	HD_LOTE_MEDIC_EXT
	where	nr_seq_entrega = nr_seq_entrega_p
	order by 1;


BEGIN

select	coalesce(sum(qt_material),0) qt_medic
into STRICT	qt_medic_w
from	hd_lote_medic_ext
where	nr_seq_entrega = nr_seq_entrega_p;

open C01;
loop
fetch C01 into
	nr_seq_lote_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	coalesce(sum(qt_medicamento),0) qt_utilizado
	into STRICT	qt_utilizado_w
	from	hd_dialise_med_ext
	where	nr_seq_lote = nr_seq_lote_w;

	qt_utilizado_ww := qt_utilizado_ww + qt_utilizado_w;

	end;
end loop;
close C01;

if (ie_tipo_dado_p = 'S') then
	qt_retorno_w := qt_medic_w - qt_utilizado_ww;
elsif (ie_tipo_dado_p = 'T') then
	qt_retorno_w := qt_medic_w;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
