CREATE OR REPLACE FUNCTION hd_obter_dados_gestao_solucao (nr_sequencia_p bigint, nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);

BEGIN
ds_retorno_w := '';
if (upper(nm_tabela_p) = 'HD_SALA_REUSO') then
	select 	substr(ds_sala_reuso,1,255)
	into STRICT	ds_retorno_w
	from	hd_sala_reuso
	where	nr_sequencia = nr_sequencia_p;
elsif (upper(nm_tabela_p) = 'HD_RECIPIENTE_SOLUCAO') then
	select 	substr(ds_recipiente,1,255)
	into STRICT	ds_retorno_w
	from	hd_recipiente_solucao
	where	nr_sequencia = nr_sequencia_p;
elsif (upper(nm_tabela_p) = 'HD_MOTIVO_DESCARTE') then
	select 	substr(ds_motivo_descarte,1,255)
	into STRICT	ds_retorno_w
	from	hd_motivo_descarte
	where	nr_sequencia = nr_sequencia_p;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
