SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_dados_maquina (nr_seq_maquina_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_maquina_w		bigint;
ds_maquina_w		varchar(80);
nr_seq_modelo_w		bigint;
ds_retorno_w		varchar(255);
qt_tempo_maquina_w	integer;
nr_seq_ponto_w		bigint;
ie_informar_ktv_w	varchar(1);
ie_informar_clearence_w	varchar(1);

/*
C      - Código máquina
D     - Descrição máquina
M     - Modelo máquina
T      - Tempo máquina
P      - Ponto de diálise
KTV - Informar KTV
CLE - Informar CLEARENCE
*/
BEGIN

select	max(cd_maquina),
	max(ds_maquina_dialise),
	max(nr_seq_modelo),
	max(qt_tempo_maquina),
	max(nr_seq_ponto),
	max(ie_informar_ktv),
	max(ie_informar_clearence)
into STRICT	cd_maquina_w,
	ds_maquina_w,
	nr_seq_modelo_w,
	qt_tempo_maquina_w,
	nr_seq_ponto_w,
	ie_informar_ktv_w,
	ie_informar_clearence_w
from	hd_maquina_dialise
where	nr_sequencia	= nr_seq_maquina_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_maquina_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_maquina_w;
elsif (ie_opcao_p = 'M') then
	ds_retorno_w	:= nr_seq_modelo_w;
elsif (ie_opcao_p = 'T') then
	ds_retorno_w	:= qt_tempo_maquina_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w	:= nr_seq_ponto_w;
elsif (ie_opcao_p = 'KTV') then
	ds_retorno_w	:= ie_informar_ktv_w;
elsif (ie_opcao_p = 'CLE') then
	ds_retorno_w	:= ie_informar_clearence_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
