SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_dados_med_ext (cd_pessoa_fisica_p text, cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_med_ext_w	bigint;
dt_ultima_admin_w	timestamp;
qt_medicamento_w	bigint;

BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_med_ext_w
from	hd_dialise_med_ext
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	nr_seq_medic = (SELECT	max(nr_sequencia)
			from	hd_medic_externo x
			where	x.cd_material = cd_material_p)
and	coalesce(ie_local,'I') = 'I';

select	max(dt_atualizacao_nrec),
	max(qt_medicamento)
into STRICT	dt_ultima_admin_w,
	qt_medicamento_w
from	hd_dialise_med_ext
where	nr_sequencia = nr_seq_med_ext_w;

if (ie_opcao_p = 'D') then
	ds_retorno_w := to_char(dt_ultima_admin_w,'dd/mm/yyyy hh24:mi:ss');
elsif (ie_opcao_p = 'Q') then
	ds_retorno_w := qt_medicamento_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
