CREATE OR REPLACE FUNCTION hd_obter_dados_modelo_rep (nr_seq_modelo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_modelo_w	varchar(255);
nr_seq_marca_w	bigint;
ds_marca_w	varchar(255);

/*
D - Descrição do modelo
CM - Código da marca
DM - Descrição da marca
*/
BEGIN

if (coalesce(nr_seq_modelo_p,0) > 0) then
	begin
	select	ds_modelo,
		nr_seq_marca,
		substr(hd_desc_marca_reprocessador(nr_seq_marca,'D'),1,255) ds_marca
	into STRICT	ds_modelo_w,
		nr_seq_marca_w,
		ds_marca_w
	from	hd_modelo_reprocessador
	where	nr_sequencia = nr_seq_modelo_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w	:= ds_modelo_w;
	elsif (ie_opcao_p = 'CM') then
		ds_retorno_w	:= nr_seq_marca_w;
	elsif (ie_opcao_p = 'DM') then
		ds_retorno_w	:= ds_marca_w;
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
