CREATE OR REPLACE FUNCTION hd_obter_desc_acesso ( nr_seq_acesso bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN

select	substr(c.ds_tecnica_acesso || ' ' || b.ds_local_acesso,1,255)
into STRICT	ds_retorno_w
FROM hd_acesso a
LEFT OUTER JOIN hd_tecnica_acesso c ON (a.nr_seq_tecnica = c.nr_sequencia)
LEFT OUTER JOIN hd_local_acesso b ON (a.nr_seq_local = b.nr_sequencia)
WHERE a.nr_sequencia		= nr_seq_acesso;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
