CREATE OR REPLACE FUNCTION hd_obter_desc_item_controle (nr_seq_item_controle_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_item_w	varchar(255);


BEGIN

if (coalesce(nr_seq_item_controle_p,0) > 0) then

	select 	substr(max(ds_item),1,80)
	into STRICT	ds_item_w
	from	hd_item_controle_exame
	where	nr_sequencia = nr_seq_item_controle_p;

end if;

return	ds_item_w;

end;
