SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_dialisador_transf ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*IE_OPCAO_P

'C'	= Código
'D'	= Descrição
*/
nr_seq_dialisador_w	bigint;
ds_dialisador_w		varchar(80);

ds_retorno_w		varchar(80);



BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_dialisador_w
from	hd_dializador
where	hd_obter_unid_atual_dialisador(nr_sequencia,'C') = hd_obter_unidade_prc(cd_pessoa_fisica_p,'C')
and	ie_status 		= 'T'
and	cd_pessoa_fisica	= cd_pessoa_fisica_p;


select	substr(obter_descricao_padrao('HD_MODELO_DIALISADOR','DS_MODELO', nr_seq_modelo),1,200)
into STRICT	ds_dialisador_w
from	hd_dializador
where	nr_sequencia	= nr_seq_dialisador_w;

if (ie_opcao_p = 'C') then
	ds_retorno_w := nr_seq_dialisador_w;
else
	ds_retorno_w := ds_dialisador_w;
end if;



return	ds_retorno_w;


End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
