SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION hd_obter_dialise_atual (cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

nr_seq_dialise_w	bigint;

BEGIN

if (ie_opcao_p = 'I') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_dialise_w
	from 	hd_dialise
	where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	--and 	dt_inicio_dialise 	is not null
	and 	coalesce(dt_fim_dialise::text, '') = ''
	and	coalesce(dt_cancelamento::text, '') = '';
elsif (ie_opcao_p = 'NI') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_dialise_w
	from 	hd_dialise
	where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p
	and 	coalesce(dt_fim_dialise::text, '') = ''
	and	coalesce(dt_cancelamento::text, '') = '';
end if;

return	nr_seq_dialise_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
