CREATE OR REPLACE FUNCTION hd_obter_dialise_dialisador ( nr_seq_dialisador_p bigint, ie_tipo_retorno_p text) RETURNS bigint AS $body$
DECLARE


/* ie_tipo_retorno_p
   D - Di√°lise
   S - Dialisador
*/
nr_seq_dialise_w		bigint;
nr_sequencia_w			bigint;


BEGIN

begin
select	max(nr_sequencia)
into STRICT	nr_seq_dialise_w
from	hd_dialise_dialisador
where	nr_seq_dialisador	= nr_seq_dialisador_p;
exception
	when others then
	nr_seq_dialise_w	:= 0;
end;

if (ie_tipo_retorno_p = 'D') then
	begin
	select	nr_seq_dialise
	into STRICT	nr_seq_dialise_w
	from	hd_dialise_dialisador
	where	nr_sequencia		= nr_seq_dialise_w;
	exception
		when others then
		nr_seq_dialise_w	:= 0;
	end;
end if;

return nr_seq_dialise_w;

end;
$body$
