CREATE OR REPLACE FUNCTION hd_obter_dose_unidade_medida (cd_pessoa_fisica_p text, ie_tipo_solucao_p text, ie_retorno_p text, nr_prescricao_p text default null) RETURNS varchar AS $body$
DECLARE


dose_retorno_w 			varchar(255);
uni_medida_retorno_w 	varchar(255);
qt_dose_w				varchar(255);
ie_unidade_medida_w		varchar(255);
nr_prescricao_w			prescr_solucao.nr_prescricao%type;
cd_pessoa_fisica_w pessoa_fisica.cd_pessoa_fisica%type;


BEGIN
dose_retorno_w := '';
uni_medida_retorno_w := '';
cd_pessoa_fisica_w := cd_pessoa_fisica_p;
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	nr_prescricao_w := nr_prescricao_p;
	if (coalesce(nr_prescricao_w::text, '') = '')then
		select	max(b.nr_prescricao)
		into STRICT	nr_prescricao_w
		from	prescr_medica b,
		  atendimento_paciente c
		where	c.nr_atendimento = b.nr_atendimento
		and	c.cd_pessoa_fisica = cd_pessoa_fisica_w
		and	(coalesce(b.dt_liberacao,b.DT_LIBERACAO_MEDICO) IS NOT NULL AND (coalesce(b.dt_liberacao,b.DT_LIBERACAO_MEDICO))::text <> '');
	end if;

	select sum(b.QT_DOSE),
	max(b.cd_unidade_medida_dose)
	into STRICT	qt_dose_w,
			ie_unidade_medida_w
	from 	hd_prescricao a,
			PRESCR_MATERIAL b,
			HD_PARAMETRO c
	where a.nr_prescricao = nr_prescricao_w
	and 	b.nr_prescricao = a.nr_prescricao
	and Obter_Se_Contido(b.CD_MATERIAL,c.CD_MATERIAIS_HEPARINA) = 'S'
	and c.CD_ESTABELECIMENTO = wheb_usuario_pck.get_cd_estabelecimento;

	uni_medida_retorno_w := ie_unidade_medida_w;
	dose_retorno_w := qt_dose_w;

	if (ie_retorno_p = 'D') then
		return dose_retorno_w;
	else
		return uni_medida_retorno_w;
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
