CREATE OR REPLACE FUNCTION hd_obter_equipe_prc ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


-- C - Código
-- D - Descrição
nr_seq_equipe_w		bigint;
ds_retorno_w		varchar(200);


BEGIN

select	max(nr_seq_equipe)
into STRICT	nr_seq_equipe_w
from	hd_equipe_paciente
where	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	dt_inicio <= clock_timestamp()
and	coalesce(dt_fim::text, '') = '';

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= nr_seq_equipe_w;
elsif (ie_opcao_p = 'D') then
	begin
	select	substr(ds_equipe,1,200)
	into STRICT	ds_retorno_w
	from	hd_equipe
	where	nr_sequencia	= nr_seq_equipe_w;
	end;
else
	ds_retorno_w	:= '';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
