CREATE OR REPLACE FUNCTION hd_obter_final_mes_ant (dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
FA - Fim mês anterior
FM - Fim mês
*/
qt_cronicos_fim_mes_ant_w	bigint;


BEGIN

if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (nm_usuario_p is not  null) and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then
	begin
	if (ie_opcao_p = 'FA') then
		select	sum(qt_cronicos_fim_mes_ant)
		into STRICT	qt_cronicos_fim_mes_ant_w
		from	w_fpr_dialise_mensal
		where	trunc(dt_referencia,'MM') = trunc(dt_inicial_p,'MM')
		and	nm_usuario = nm_usuario_p;
	elsif (ie_opcao_p = 'FM') then
		select	sum(qt_cronicos_fim_mes)
		into STRICT	qt_cronicos_fim_mes_ant_w
		from	w_fpr_dialise_mensal
		where	trunc(dt_referencia,'MM') = trunc(dt_final_p,'MM')
		and	nm_usuario = nm_usuario_p;
	end if;
	end;
end if;

return qt_cronicos_fim_mes_ant_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
