CREATE OR REPLACE FUNCTION hd_obter_formula_tgu_pcr ( nr_seq_grid_p bigint, cd_pessoa_fisica_p text, qt_ureia_urinaria_p bigint, qt_volume_urinario_p bigint, qt_ureia_dialisato_p bigint, qt_volume_dialisato_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_TGU_w	double precision;
qt_PCR_w	double precision;
qt_PCR_peso_w	double precision;
dt_referencia_w	timestamp;
qt_peso_w		double precision;
qt_retorno_w	double precision;
qt_ureia_urinaria_w double precision;
qt_volume_urinario_w double precision;
qt_ureia_dialisato_w double precision;
qt_volume_dialisato_w double precision;


BEGIN
/*select 	dt_referencia
into	dt_referencia_w
from	HD_EXAME_GRID_CONTROLE
where 	nr_sequencia = nr_seq_grid_p;	*/
/*select 	round(sum(nvl(qt_peso_pos,0))/count(*),2)
into	peso_pos_w
from	hd_dialise
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	qt_peso_pos > 0
and	trunc(dt_dialise,'month') = trunc(dt_referencia_w,'month');*/
select	coalesce((obter_ult_resultado_data_lab(cd_pessoa_fisica_p, nr_seq_grid_p,qt_ureia_urinaria_p)*100)/(obter_ult_resultado_data_lab(cd_pessoa_fisica_p, nr_seq_grid_p,qt_volume_urinario_p)),0),
	coalesce(obter_ult_resultado_data_lab(cd_pessoa_fisica_p, nr_seq_grid_p,qt_volume_urinario_p),0),
	coalesce(obter_ult_resultado_data_lab(cd_pessoa_fisica_p, nr_seq_grid_p,qt_ureia_dialisato_p),0),
	coalesce(obter_ult_resultado_data_lab(cd_pessoa_fisica_p, nr_seq_grid_p,qt_volume_dialisato_p),0)
into STRICT	qt_ureia_urinaria_w,
	qt_volume_urinario_w,
	qt_ureia_dialisato_w,
	qt_volume_dialisato_w
;







select	max(qt_peso)
into STRICT	qt_peso_w
from	pessoa_fisica
where 	cd_pessoa_fisica = cd_pessoa_fisica_p;

--Fórmula TGU
select	round(((qt_ureia_urinaria_w * 0.49) * qt_volume_urinario_w/100 + (qt_ureia_dialisato_w * 0.49) * qt_volume_dialisato_w/100)/1440,2)
into STRICT	qt_TGU_w
;


--Fórmula PCR
select	round(((qt_TGU_w * 1.44) + 1.46) * 10.76,2)
into STRICT	qt_PCR_w
;

--PCR corrigida em gr/kg/dia
select	round((qt_PCR_w / qt_peso_w)::numeric,2)
into STRICT	qt_PCR_peso_w
;

if (ie_opcao_p = 'TGU') then
	qt_retorno_w := qt_TGU_w;
elsif (ie_opcao_p = 'PCR') then
	qt_retorno_w := qt_PCR_w;
elsif (ie_opcao_p = 'PCRP') then
	qt_retorno_w := qt_PCR_peso_w;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
