CREATE OR REPLACE FUNCTION hd_obter_gpid ( nr_seq_dialise_p bigint, cd_pessoa_fisica_p text, qt_peso_pre_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_ultima_dialise_w	bigint;
qt_peso_ultima_w		real;
qt_gpid_w		double precision;


BEGIN

/* Pega a sequência da última diálise */

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_ultima_dialise_w
from	hd_dialise
where	nr_sequencia	<> nr_seq_dialise_p
and	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	(qt_peso_pre IS NOT NULL AND qt_peso_pre::text <> '');

/* Pega o peso da última diálise */

begin
select	qt_peso_pos
into STRICT	qt_peso_ultima_w
from	hd_dialise
where	nr_sequencia	= nr_seq_ultima_dialise_w;
exception
	when others then
	qt_peso_ultima_w	:= 0;
end;

if (qt_peso_ultima_w = 0) then
	qt_gpid_w	:= 0;
else
	qt_gpid_w	:= ((qt_peso_pre_p/qt_peso_ultima_w)*100)-100;
end if;

return qt_gpid_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
